targets.expr:
round(
  sum(
    rate(
      ticket_backend_echo_requests_total{
        job=~"$job",
        instance=~"$instance",
        url=~"$handler"
      }[$__rate_interval]
    )
  ), 0.001
)

sum(
  rate(
    ticket_backend_echo_requests_total{
      job=~"$job",
      instance=~"$instance",
      url=~"$handler",
      code!~"[4-5].*"
    }[$__rate_interval]
  )
) /
sum(
  rate(
    ticket_backend_echo_requests_total{
      job=~"$job",
      instance=~"$instance",
      url=~"$handler",
    }[$__rate_interval]
  )
)

histogram_quantile(0.95,
  sum (
    irate(
      ticket_backend_echo_request_duration_seconds_bucket{
        job=~"$job",
        instance=~"$instance",
        url=~"$handler"
      }[$__rate_interval]
    )
  ) by (job, le)
)
