targets.expr:
round(
  sum(
    rate(
      paymenthttp_requests_total{
        job=~"$job",
        instance=~"$instance",
        route=~"$handler"
      }[$__rate_interval]
    )
  ), 0.001
)

sum(
  rate(
    paymenthttp_requests_total{
      job=~"$job",
      instance=~"$instance",
      route=~"$handler",
      status!~"[4-5].*"
    }[$__rate_interval]
  )
) /
sum(
  rate(
    paymenthttp_requests_total{
      job=~"$job",
      instance=~"$instance",
      route=~"$handler",
    }[$__rate_interval]
  )
)

histogram_quantile(0.95,
  sum (
    irate(
      paymenthttp_request_duration_seconds_bucket{
        job=~"$job",
        instance=~"$instance",
        route=~"$handler"
      }[$__rate_interval]
    )
  ) by (job, le)
)
